(()=>{"use strict";(e=>{const t=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),i=document.getElementById("timer-seconds"),o=()=>{let e=(new Date("30 october 2021").getTime()-(new Date).getTime())/1e3;return{timeRemaining:e,hours:Math.floor(e/60/60),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}},m=()=>{let e=o();t.textContent=("0"+e.hours).slice(-2),n.textContent=("0"+e.minutes).slice(-2),i.textContent=("0"+e.seconds).slice(-2)};setInterval((()=>{let e=o();e.timeRemaining>0?m():0===e.timeRemaining&&clearInterval(m)}),1e3)})()})();