(()=>{"use strict";(e=>{const t=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds"),s=()=>{let e=(new Date("30 october 2021").getTime()-(new Date).getTime())/1e3;return{timeRemaining:e,hours:Math.floor(e/60/60),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}};setInterval((()=>{s().timeRemaining>0&&(()=>{let e=s();t.textContent=("0"+e.hours).slice(-2),n.textContent=("0"+e.minutes).slice(-2),o.textContent=("0"+e.seconds).slice(-2)})()}),1e3)})()})();